INSERT IGNORE INTO `email_templates` VALUES (3,'loan_approved','de_DE','‚úÖ Ihre Ausleihanfrage wurde genehmigt!','<h2>Ihre Ausleihanfrage wurde genehmigt!</h2>\n<p>Guten Tag {{user_name}},</p>\n<p>wir freuen uns, Ihnen mitteilen zu k√∂nnen, dass Ihre Ausleihanfrage <strong>genehmigt</strong> wurde!</p>\n<p><strong>Ausleihdetails:</strong></p>\n<ul>\n    <li>Buch: {{book_title}}</li>\n    <li>Ausleihdatum: {{start_date}}</li>\n    <li>R√ºckgabedatum: {{end_date}}</li>\n    <li>Dauer: {{loan_days}} Tage</li>\n</ul>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Abholung des Buches</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p><strong>Wichtig:</strong> Bitte geben Sie das Buch bis zum R√ºckgabedatum zur√ºck. Sie erhalten einige Tage vor Ablauf der Frist eine Erinnerung.</p>\n<p>Viel Freude beim Lesen!</p>','Wird dem Benutzer gesendet, wenn ein Administrator eine Ausleihanfrage genehmigt.',1,NULL,NULL);
INSERT IGNORE INTO `email_templates` VALUES (4,'loan_rejected','de_DE','‚ùå Ihre Ausleihanfrage wurde nicht genehmigt','<h2>Ihre Ausleihanfrage wurde nicht genehmigt</h2>\n<p>Guten Tag {{user_name}},</p>\n<p>leider m√ºssen wir Ihnen mitteilen, dass Ihre Ausleihanfrage f√ºr das Buch <strong>\"{{book_title}}\"</strong> nicht genehmigt wurde.</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Grund:</strong></p>\n    <p>{{rejection_reason}}</p>\n</div>\n<p>Wenn Sie Fragen haben oder weitere Informationen ben√∂tigen, z√∂gern Sie bitte nicht, uns zu kontaktieren.</p>\n<p>Mit freundlichen Gr√º√üen,<br>Das Bibliotheksteam</p>','Wird dem Benutzer gesendet, wenn ein Administrator eine Ausleihanfrage ablehnt.',1,NULL,NULL);
INSERT IGNORE INTO `email_templates` VALUES (5,'loan_expiring_warning','de_DE','‚ö†Ô∏è Ihre Ausleihe l√§uft bald ab','<h2>Erinnerung an den Ablauf der Ausleihe</h2>\n<p>Guten Tag {{user_name}},</p>\n<p>wir m√∂chten Sie daran erinnern, dass Ihre Ausleihe bald abl√§uft:</p>\n<p><strong>Ausleihdetails:</strong></p>\n<ul>\n    <li>Buch: {{book_title}}</li>\n    <li>Ablaufdatum: {{due_date}}</li>\n    <li>Verbleibende Tage: {{days_remaining}}</li>\n</ul>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Handlung erforderlich</strong></p>\n    <p>Um Geb√ºhren zu vermeiden, geben Sie das Buch bitte bis zum F√§lligkeitsdatum zur√ºck oder kontaktieren Sie die Bibliothek f√ºr eine m√∂gliche Verl√§ngerung.</p>\n</div>\n<p>Vielen Dank f√ºr Ihre Mithilfe!</p>','E-Mail an den Benutzer 3 Tage vor Ablauf der Leihfrist.',1,NULL,NULL);
INSERT IGNORE INTO `email_templates` VALUES (6,'loan_overdue_admin','de_DE','Ausleihe #{{loan_id}} √ºberf√§llig','<h2>√úberf√§llige Ausleihe</h2>\n<p>Die Ausleihe <strong>#{{loan_id}}</strong> hat den Status <strong>√ºberf√§llig</strong> erreicht.</p>\n<ul>\n  <li><strong>Buch:</strong> {{book_title}}</li>\n  <li><strong>Benutzer:</strong> {{user_name}} ({{user_email}})</li>\n  <li><strong>Ausleihdatum:</strong> {{loan_date}}</li>\n  <li><strong>R√ºckgabedatum:</strong> {{due_date}}</li>\n</ul>\n<p>Bitte kontaktieren Sie den Benutzer, um die R√ºckgabe des Buches anzufordern.</p>','Benachrichtigt Administratoren, wenn eine Ausleihe √ºberf√§llig wird.',1,NULL,NULL);
INSERT IGNORE INTO `email_templates` VALUES (7,'loan_overdue_notification','de_DE','üö® √úberf√§llige Ausleihe - Handlung erforderlich','<h2>√úberf√§llige Ausleihe</h2>\n<p>Guten Tag {{user_name}},</p>\n<p>Ihre Ausleihe ist √ºberf√§llig und muss umgehend zur√ºckgegeben werden:</p>\n<p><strong>Ausleihdetails:</strong></p>\n<ul>\n    <li>Buch: {{book_title}}</li>\n    <li>R√ºckgabedatum: {{due_date}}</li>\n    <li>√úberf√§llig seit: {{days_overdue}} Tagen</li>\n</ul>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>üö® Dringende Handlung erforderlich</strong></p>\n    <p>Das Buch muss umgehend zur√ºckgegeben werden. Versp√§tete R√ºckgaben k√∂nnen zu Geb√ºhren und einer Sperrung des Dienstes f√ºhren.</p>\n</div>\n<p>Bitte kontaktieren Sie die Bibliothek umgehend, um diese Situation zu kl√§ren.</p>','E-Mail an den Benutzer, wenn die Ausleihe √ºberf√§llig ist.',1,NULL,NULL);
