INSERT IGNORE INTO `email_templates` VALUES (9,'user_account_approved','de_DE','Konto genehmigt - Willkommen in der Bibliothek!','<h2>Ihr Konto wurde genehmigt!</h2>\n<p>Guten Tag {{first_name}} {{last_name}},</p>\n<p>wir freuen uns, Ihnen mitteilen zu k√∂nnen, dass Ihr Konto von einem Administrator genehmigt wurde.</p>\n<p>Sie k√∂nnen sich nun anmelden und B√ºcher vormerken!</p>\n<p><strong>Ihre Kontodaten:</strong></p>\n<ul>\n    <li>E-Mail: {{email}}</li>\n    <li>Ausweisnummer: {{card_code}}</li>\n</ul>\n<p><a href=\"{{login_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Jetzt anmelden</a></p>\n<p>Willkommen in unserer digitalen Bibliothek!</p>','E-Mail, die gesendet wird, wenn ein Administrator das Konto direkt genehmigt, ohne E-Mail-Verifizierung zu verlangen.',1,NULL,NULL);
INSERT IGNORE INTO `email_templates` VALUES (10,'user_activation_with_verification','de_DE','Konto aktivieren - E-Mail best√§tigen','<h2>Ihr Konto wurde genehmigt!</h2>\n<p>Guten Tag {{first_name}} {{last_name}},</p>\n<p>wir freuen uns, Ihnen mitteilen zu k√∂nnen, dass Ihr Konto von einem Administrator genehmigt wurde.</p>\n<p>üìß <strong>Best√§tigen Sie Ihre E-Mail-Adresse</strong></p>\n<p>Um die Aktivierung abzuschlie√üen, klicken Sie auf die Schaltfl√§che unten, um Ihre E-Mail-Adresse zu best√§tigen:</p>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{verification_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">‚úÖ E-Mail best√§tigen</a>\n</p>\n<p><strong>Ihre Kontodaten:</strong></p>\n<ul>\n    <li>E-Mail: {{email}}</li>\n    <li>Ausweisnummer: {{card_code}}</li>\n</ul>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Wichtig</strong></p>\n    <p>Der Best√§tigungslink ist 7 Tage g√ºltig. Nach der Best√§tigung k√∂nnen Sie sich anmelden und B√ºcher vormerken.</p>\n</div>\n<p>Wenn Sie diese Registrierung nicht beantragt haben, k√∂nnen Sie diese E-Mail ignorieren.</p>','E-Mail, die gesendet wird, wenn ein Administrator das Konto genehmigt und eine E-Mail-Verifizierung erfordert.',1,NULL,NULL);
