INSERT INTO `cms_pages` VALUES (1,'about-us','en_US','About Us','<h2>Welcome to Pinakes</h2>\n<p>Pinakes is a modern, intuitive, and completely free library management system. Built to simplify book cataloging, loan management, and library administration of all sizes.</p>\n\n<h3>🎯 Our Mission</h3>\n<p>Making library management accessible to everyone, without licensing costs and without constraints. Pinakes is 100% open source, free to use by everyone.</p>\n\n<h3>✨ Key Features</h3>\n<ul>\n    <li><strong>Smart Cataloging:</strong> Automatically import data from ISBN, organize by categories, authors, and custom classifications</li>\n    <li><strong>Loan Management:</strong> Complete system for managing loans, due dates, renewals, and returns</li>\n    <li><strong>Automatic Notifications:</strong> Email reminders for loan due dates and user communications</li>\n    <li><strong>Modern Interface:</strong> Responsive design optimized for desktop, tablet, and smartphone</li>\n    <li><strong>100% Open Source:</strong> Free code, customizable, and no hidden costs</li>\n</ul>\n\n<h3>🚀 Why Choose Pinakes?</h3>\n<p>Pinakes was born from the need to provide a professional, reliable, and free tool for managing school, association, and private libraries. No need to be tech-savvy: the intuitive interface allows anyone to start immediately.</p>\n\n<h3>📖 Get Started Today</h3>\n<p>Customize this page with information about your library, your team, and your services. Access the <a href=\"/admin/cms/about-us\">admin panel</a> to edit this content and make it unique.</p>\n\n<p><em>This is placeholder content. Customize it to tell your library''s story!</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `cms_pages` VALUES (2,'privacy-policy','en_US','Privacy Policy','<h2>Privacy Policy</h2>\n<p>This is a placeholder page for the Privacy Policy. Customize this content according to applicable regulations (GDPR, Privacy Code) and your library''s specific needs.</p>\n\n<h3>1. Data Controller</h3>\n<p>[Insert here the data controller information: name, address, email, phone]</p>\n\n<h3>2. Data Collected</h3>\n<p>Personal data collected through this system includes:</p>\n<ul>\n    <li>Personal information (name, surname, date of birth)</li>\n    <li>Contact information (email, phone, address)</li>\n    <li>Data related to loans made</li>\n</ul>\n\n<h3>3. Purpose of Processing</h3>\n<p>Data is processed exclusively for:</p>\n<ul>\n    <li>User registry management</li>\n    <li>Library loan management</li>\n    <li>Sending communications related to loans (due dates, renewals)</li>\n</ul>\n\n<h3>4. Data Subject Rights</h3>\n<p>Users have the right to access their data, request its correction, deletion, or limitation of processing, as provided by EU Regulation 2016/679 (GDPR).</p>\n\n<p><strong>IMPORTANT:</strong> This is a sample template. Consult a legal professional to draft a Privacy Policy compliant with current regulations.</p>\n\n<p><em>Access the <a href=\"/admin/cms/privacy-policy\">admin panel</a> to customize this page.</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `email_templates` VALUES (28,'admin_new_registration','en_US','👤 New User Registration Request','\n            <h2>New Registration Request</h2>\n            <p>A new user has requested access to the library system:</p>\n            <p><strong>User Details:</strong></p>\n            <ul>\n                <li>Name: {{nome}} {{cognome}}</li>\n                <li>Email: {{email}}</li>\n                <li>Card Code: {{codice_tessera}}</li>\n                <li>Data richiesta: {{data_registrazione}}</li>\n            </ul>\n            <p><a href=\"{{admin_users_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Manage Users</a></p>\n        ','Email inviata agli admin quando un nuovo utente si registra.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (17,'admin_new_registration','it_IT','👤 Nuova richiesta di registrazione','\n            <h2>Nuova richiesta di registrazione</h2>\n            <p>Un nuovo utente ha richiesto l\'accesso al sistema biblioteca:</p>\n            <p><strong>Dettagli utente:</strong></p>\n            <ul>\n                <li>Nome: {{nome}} {{cognome}}</li>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n                <li>Data richiesta: {{data_registrazione}}</li>\n            </ul>\n            <p><a href=\"{{admin_users_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Gestisci Utenti</a></p>\n        ','Email inviata agli admin quando un nuovo utente si registra.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (32,'admin_new_review','en_US','⭐ New Review to Approve','<h2>New Review to Approve</h2>\n<p>A new review has been received for the book:</p>\n<p><strong>Book:</strong> {{libro_titolo}}</p>\n<div style=\"background-color: #fff7ed; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #f59e0b;\">\n    <p><strong>Rating:</strong> {{stelle}} stelle ⭐</p>\n    <p><strong>Utente:</strong> {{utente_nome}} ({{utente_email}})</p>\n    <p><strong>Review Date:</strong> {{data_recensione}}</p>\n    <p><strong>Titolo:</strong> {{titolo_recensione}}</p>\n    <p><strong>Descrizione:</strong></p>\n    <p style=\"white-space: pre-line;\">{{descrizione_recensione}}</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{link_approvazione}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">Gestisci Review</a>\n</p>\n<p><em>Per approvare o rifiutare questa recensione, accedi al pannello admin.</em></p>','Inviata agli amministratori quando viene ricevuta una nuova recensione da approvare.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (21,'admin_new_review','it_IT','⭐ Nuova recensione da approvare','<h2>Nuova recensione da approvare</h2>\n<p>È stata ricevuta una nuova recensione per il libro:</p>\n<p><strong>Libro:</strong> {{libro_titolo}}</p>\n<div style=\"background-color: #fff7ed; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #f59e0b;\">\n    <p><strong>Valutazione:</strong> {{stelle}} stelle ⭐</p>\n    <p><strong>Utente:</strong> {{utente_nome}} ({{utente_email}})</p>\n    <p><strong>Data recensione:</strong> {{data_recensione}}</p>\n    <p><strong>Titolo:</strong> {{titolo_recensione}}</p>\n    <p><strong>Descrizione:</strong></p>\n    <p style=\"white-space: pre-line;\">{{descrizione_recensione}}</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{link_approvazione}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">Gestisci Recensione</a>\n</p>\n<p><em>Per approvare o rifiutare questa recensione, accedi al pannello admin.</em></p>','Inviata agli amministratori quando viene ricevuta una nuova recensione da approvare.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (31,'loan_approved','en_US','✅ Your Loan Request Has Been Approved!','<h2>Your Loan Request Has Been Approved!</h2>\n<p>Hello {{utente_nome}},</p>\n<p>We are pleased to inform you that your loan request has been <strong>approved</strong>!</p>\n<p><strong>Loan Details:</strong></p>\n<ul>\n    <li>Book: {{libro_titolo}}</li>\n    <li>Loan Start Date: {{data_inizio}}</li>\n    <li>Due Date: {{data_fine}}</li>\n    <li>Durata: {{days_prestito}} days</li>\n</ul>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>📦 Ritiro del libro</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p><strong>Importante:</strong> Ricorda di restituire il libro entro la data di scadenza. Riceverai un promemoria alcuni days prima della scadenza.</p>\n<p>Buona lettura!</p>','Inviata all\'utente quando un amministratore approva una richiesta di prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (20,'loan_approved','it_IT','✅ La tua richiesta di prestito è stata approvata!','<h2>La tua richiesta di prestito è stata approvata!</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Siamo lieti di informarti che la tua richiesta di prestito è stata <strong>approvata</strong>!</p>\n<p><strong>Dettagli del prestito:</strong></p>\n<ul>\n    <li>Libro: {{libro_titolo}}</li>\n    <li>Data inizio prestito: {{data_inizio}}</li>\n    <li>Data scadenza: {{data_fine}}</li>\n    <li>Durata: {{giorni_prestito}} giorni</li>\n</ul>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>📦 Ritiro del libro</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p><strong>Importante:</strong> Ricorda di restituire il libro entro la data di scadenza. Riceverai un promemoria alcuni giorni prima della scadenza.</p>\n<p>Buona lettura!</p>','Inviata all\'utente quando un amministratore approva una richiesta di prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (26,'loan_expiring_warning','en_US','⚠️ Your Loan is About to Expire','\n            <h2>Loan Expiration Reminder</h2>\n            <p>Hello {{utente_nome}},</p>\n            <p>We remind you that your loan is about to expire:</p>\n            <p><strong>Loan details:</strong></p>\n            <ul>\n                <li>Book: {{libro_titolo}}</li>\n                <li>Expiration date: {{data_scadenza}}</li>\n                <li>Days remaining: {{giorni_rimasti}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏰ Action Required</strong></p>\n                <p>To avoid penalties, return the book by the due date or contact the library for a possible renewal.</p>\n            </div>\n            <p>Thank you for your cooperation!</p>\n        ','Email inviata all\'utente 3 giorni prima della scadenza del prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (15,'loan_expiring_warning','it_IT','⚠️ Il tuo prestito sta per scadere','\n            <h2>Promemoria scadenza prestito</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Ti ricordiamo che il tuo prestito sta per scadere:</p>\n            <p><strong>Dettagli prestito:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Data scadenza: {{data_scadenza}}</li>\n                <li>Giorni rimasti: {{giorni_rimasti}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏰ Azione richiesta</strong></p>\n                <p>Per evitare penali, restituisci il libro entro la data di scadenza o contatta la biblioteca per un eventuale rinnovo.</p>\n            </div>\n            <p>Grazie per la collaborazione!</p>\n        ','Email inviata all\'utente 3 giorni prima della scadenza del prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (30,'loan_overdue_admin','en_US','Loan #{prestito_id} Overdue','<h2>Overdue Loan</h2>\n<p>Loan <strong>#{{prestito_id}}</strong> has entered status <strong>overdue</strong>.</p>\n<ul>\n  <li><strong>Book:</strong> {{libro_titolo}}</li>\n  <li><strong>User:</strong> {{utente_nome}} ({{utente_email}})</li>\n  <li><strong>Loan Date:</strong> {{data_prestito}}</li>\n  <li><strong>Due Date:</strong> {{data_scadenza}}</li>\n</ul>\n<p>Intervieni per contattare l\'utente e sollecitare la restituzione.</p>','Avvisa gli amministratori quando un prestito entra in ritardo.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (19,'loan_overdue_admin','it_IT','Prestito #{prestito_id} in ritardo','<h2>Prestito in ritardo</h2>\n<p>Il prestito <strong>#{{prestito_id}}</strong> è entrato nello stato <strong>in ritardo</strong>.</p>\n<ul>\n  <li><strong>Libro:</strong> {{libro_titolo}}</li>\n  <li><strong>Utente:</strong> {{utente_nome}} ({{utente_email}})</li>\n  <li><strong>Data prestito:</strong> {{data_prestito}}</li>\n  <li><strong>Data scadenza:</strong> {{data_scadenza}}</li>\n</ul>\n<p>Intervieni per contattare l\'utente e sollecitare la restituzione.</p>','Avvisa gli amministratori quando un prestito entra in ritardo.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (27,'loan_overdue_notification','en_US','🚨 Overdue Loan - Action Required','\n            <h2>Overdue Loan</h2>\n            <p>Hello {{utente_nome}},</p>\n            <p>Your loan is overdue and must be returned immediately:</p>\n            <p><strong>Loan details:</strong></p>\n            <ul>\n                <li>Book: {{libro_titolo}}</li>\n                <li>Due date: {{data_scadenza}}</li>\n                <li>Days overdue: {{giorni_ritardo}}</li>\n            </ul>\n            <div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n                <p><strong>🚨 Urgent Action Required</strong></p>\n                <p>The book must be returned immediately. Late returns may result in penalties and suspension of service.</p>\n            </div>\n            <p>Contact the library immediately to resolve this situation.</p>\n        ','Email inviata all\'utente quando il prestito è scaduto.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (16,'loan_overdue_notification','it_IT','🚨 Prestito scaduto - Azione richiesta','\n            <h2>Prestito scaduto</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Il tuo prestito è scaduto e deve essere restituito immediatamente:</p>\n            <p><strong>Dettagli prestito:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Data scadenza: {{data_scadenza}}</li>\n                <li>Giorni di ritardo: {{giorni_ritardo}}</li>\n            </ul>\n            <div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n                <p><strong>🚨 Azione urgente richiesta</strong></p>\n                <p>Il libro deve essere restituito immediatamente. Il ritardo nella restituzione può comportare sanzioni e la sospensione del servizio.</p>\n            </div>\n            <p>Contatta immediatamente la biblioteca per risolvere la situazione.</p>\n        ','Email inviata all\'utente quando il prestito è scaduto.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (25,'loan_request_notification','en_US','📚 New Loan Request','\n            <h2>New Loan Request</h2>\n            <p>A new loan request has been received:</p>\n            <p><strong>Details:</strong></p>\n            <ul>\n                <li>Book: {{libro_titolo}}</li>\n                <li>User: {{utente_nome}} ({{utente_email}})</li>\n                <li>Requested start date: {{data_inizio}}</li>\n                <li>Requested end date: {{data_fine}}</li>\n                <li>Request date: {{data_richiesta}}</li>\n            </ul>\n            <p><a href=\"{{approve_url}}\" style=\"background-color: #10b981; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Manage Request</a></p>\n        ','Email inviata agli admin quando un utente richiede un prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (14,'loan_request_notification','it_IT','📚 Nuova richiesta di prestito','\n            <h2>Nuova richiesta di prestito</h2>\n            <p>È stata ricevuta una nuova richiesta di prestito:</p>\n            <p><strong>Dettagli:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Utente: {{utente_nome}} ({{utente_email}})</li>\n                <li>Data richiesta inizio: {{data_inizio}}</li>\n                <li>Data richiesta fine: {{data_fine}}</li>\n                <li>Data richiesta: {{data_richiesta}}</li>\n            </ul>\n            <p><a href=\"{{approve_url}}\" style=\"background-color: #10b981; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Gestisci Richiesta</a></p>\n        ','Email inviata agli admin quando un utente richiede un prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (23,'user_account_approved','en_US','Account Approved - Welcome to the Library!','\n            <h2>Your account has been approved!</h2>\n            <p>Hello {{nome}} {{cognome}},</p>\n            <p>We are pleased to inform you that your account has been approved by an administrator.</p>\n            <p>You can now access the system and start booking books!</p>\n            <p><strong>Your account details:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Card code: {{codice_tessera}}</li>\n            </ul>\n            <p><a href=\"{{login_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Login Now</a></p>\n            <p>Welcome to our digital library!</p>\n        ','Email inviata quando un admin approva direttamente l\'account senza richiedere verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (12,'user_account_approved','it_IT','Account approvato - Benvenuto in biblioteca!','\n            <h2>Il tuo account è stato approvato!</h2>\n            <p>Ciao {{nome}} {{cognome}},</p>\n            <p>Siamo lieti di informarti che il tuo account è stato approvato da un amministratore.</p>\n            <p>Ora puoi accedere al sistema e iniziare a prenotare libri!</p>\n            <p><strong>Dettagli del tuo account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n            </ul>\n            <p><a href=\"{{login_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Accedi ora</a></p>\n            <p>Benvenuto nella nostra biblioteca digitale!</p>\n        ','Email inviata quando un admin approva direttamente l\'account senza richiedere verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (24,'user_activation_with_verification','en_US','Activate Your Account - Verify Email','\n            <h2>Your account has been approved!</h2>\n            <p>Hello {{nome}} {{cognome}},</p>\n            <p>Siamo lieti di informarti che il tuo account è stato approvato da un amministratore.</p>\n            <p>📧 <strong>Verifica la tua email</strong></p>\n            <p>Per completare l\'attivazione, clicca sul pulsante qui sotto per verificare il tuo indirizzo email:</p>\n            <p style=\"text-align: center; margin: 30px 0;\">\n                <a href=\"{{verification_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">✅ Verify Email</a>\n            </p>\n            <p><strong>Your account details:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Card code: {{codice_tessera}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏰ Importante</strong></p>\n                <p>Il link di verifica è valido per 7 giorni. Dopo la verifica potrai accedere al sistema e prenotare libri.</p>\n            </div>\n            <p>Se non hai richiesto questa registrazione, puoi ignorare questa email.</p>\n        ','Email inviata quando un admin approva l\'account richiedendo la verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (13,'user_activation_with_verification','it_IT','Attiva il tuo account - Verifica email','\n            <h2>Il tuo account è stato approvato!</h2>\n            <p>Ciao {{nome}} {{cognome}},</p>\n            <p>Siamo lieti di informarti che il tuo account è stato approvato da un amministratore.</p>\n            <p>📧 <strong>Verifica la tua email</strong></p>\n            <p>Per completare l\'attivazione, clicca sul pulsante qui sotto per verificare il tuo indirizzo email:</p>\n            <p style=\"text-align: center; margin: 30px 0;\">\n                <a href=\"{{verification_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">✅ Verifica Email</a>\n            </p>\n            <p><strong>Dettagli del tuo account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏰ Importante</strong></p>\n                <p>Il link di verifica è valido per 7 giorni. Dopo la verifica potrai accedere al sistema e prenotare libri.</p>\n            </div>\n            <p>Se non hai richiesto questa registrazione, puoi ignorare questa email.</p>\n        ','Email inviata quando un admin approva l\'account richiedendo la verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (22,'user_registration_pending','en_US','Registration Received - Pending Approval','\n            <h2>Welcome {{nome}} {{cognome}}!</h2>\n            <p>Your registration request has been received successfully.</p>\n            <p><strong>Account details:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Card code: {{codice_tessera}}</li>\n                <li>Registration date: {{data_registrazione}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏳ Account pending approval</strong></p>\n                <p>Your account is pending approval by an administrator.\n                You will receive a confirmation email once your account has been activated.</p>\n            </div>\n            <p>Thank you for choosing our library system!</p>\n        ','Email inviata all\'utente dopo la registrazione, quando l\'account è in attesa di approvazione.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (11,'user_registration_pending','it_IT','Registrazione ricevuta - In attesa di approvazione','\n            <h2>Benvenuto {{nome}} {{cognome}}!</h2>\n            <p>La tua richiesta di registrazione è stata ricevuta con successo.</p>\n            <p><strong>Dettagli account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n                <li>Data registrazione: {{data_registrazione}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>⏳ Account in attesa di approvazione</strong></p>\n                <p>Il tuo account è in attesa di approvazione da parte di un amministratore.\n                Riceverai una email di conferma una volta che l\'account sarà stato attivato.</p>\n            </div>\n            <p>Grazie per aver scelto il nostro sistema biblioteca!</p>\n        ','Email inviata all\'utente dopo la registrazione, quando l\'account è in attesa di approvazione.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (29,'wishlist_book_available','en_US','📖 Book from Your Wishlist Now Available!','\n            <h2>Good News!</h2>\n            <p>Hello {{utente_nome}},</p>\n            <p>The book you added to your wishlist is now available for loan:</p>\n            <div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n                <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{libro_titolo}}</h3>\n                <p style=\"margin: 5px 0;\"><strong>Author:</strong> {{libro_autore}}</p>\n                <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{libro_isbn}}</p>\n                <p style=\"margin: 5px 0;\"><strong>Available from:</strong> {{data_disponibilita}}</p>\n            </div>\n            <div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>✨ Book Now!</strong></p>\n                <p>The book is now available for booking. Hurry before someone else books it!</p>\n            </div>\n            <p style=\"text-align: center;\">\n                <a href=\"{{book_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">📚 Book Now</a>\n                <a href=\"{{wishlist_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">❤️ Manage Wishlist</a>\n            </p>\n            <p><em>This book has been automatically removed from your wishlist.</em></p>\n        ','Email inviata quando un libro della wishlist diventa disponibile.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (18,'wishlist_book_available','it_IT','📖 Libro della tua wishlist ora disponibile!','\n            <h2>Buone notizie!</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Il libro che hai aggiunto alla tua wishlist è ora disponibile per il prestito:</p>\n            <div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n                <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{libro_titolo}}</h3>\n                <p style=\"margin: 5px 0;\"><strong>Autore:</strong> {{libro_autore}}</p>\n                <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{libro_isbn}}</p>\n                <p style=\"margin: 5px 0;\"><strong>Disponibile da:</strong> {{data_disponibilita}}</p>\n            </div>\n            <div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>✨ Prenota subito!</strong></p>\n                <p>Il libro è ora disponibile per la prenotazione. Affrettati prima che qualcun altro lo prenoti!</p>\n            </div>\n            <p style=\"text-align: center;\">\n                <a href=\"{{book_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">📚 Prenota Ora</a>\n                <a href=\"{{wishlist_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">❤️ Gestisci Wishlist</a>\n            </p>\n            <p><em>Questo libro è stato automaticamente rimosso dalla tua wishlist.</em></p>\n        ','Email inviata quando un libro della wishlist diventa disponibile.',1,NULL,NULL);
