INSERT INTO `cms_pages` VALUES (1,'about-us','en_US','About Us','<h2>Welcome to Pinakes</h2>\n<p>Pinakes is a modern, intuitive, and completely free library management system. Built to simplify book cataloging, loan management, and library administration of any size.</p>\n\n<h3>üéØ Our Mission</h3>\n<p>To make library management accessible to everyone, without license fees or restrictions. Pinakes is 100% open source, free and libre for everyone.</p>\n\n<h3>‚ú® Key Features</h3>\n<ul>\n    <li><strong>Smart Cataloging:</strong> Automatically import data from ISBN, organize by categories, authors and custom classifications</li>\n    <li><strong>Loan Management:</strong> Complete system for managing loans, due dates, renewals and returns</li>\n    <li><strong>Automatic Notifications:</strong> Email reminders for loan due dates and user communications</li>\n    <li><strong>Modern Interface:</strong> Responsive design optimized for desktop, tablet and smartphone</li>\n    <li><strong>100% Open Source:</strong> Free code, customizable and no hidden costs</li>\n</ul>\n\n<h3>üöÄ Why Choose Pinakes?</h3>\n<p>Pinakes was born from the need to provide a professional, reliable and free tool for managing school libraries, associations and private collections. You don''t need to be an IT expert: the intuitive interface allows anyone to start immediately.</p>\n\n<h3>üìñ Get Started Today</h3>\n<p>Customize this page with information about your library, your team and your services. Access the <a href=\"/admin/cms/about-us\">admin panel</a> to edit this content and make it unique.</p>\n\n<p><em>This is sample content. Customize it to tell the story of your library!</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `cms_pages` VALUES (2,'privacy-policy','en_US','Privacy Policy','<h2>Privacy Policy</h2>\n<p>This is a placeholder page for the Privacy Policy. Customize this content according to applicable regulations (GDPR, CCPA) and the specific needs of your library.</p>\n\n<h3>1. Data Controller</h3>\n<p>[Enter data controller details here: name, address, email, phone]</p>\n\n<h3>2. Data Collected</h3>\n<p>Personal data collected through this system includes:</p>\n<ul>\n    <li>Personal details (name, surname, date of birth)</li>\n    <li>Contact details (email, phone, address)</li>\n    <li>Data related to loans made</li>\n</ul>\n\n<h3>3. Purpose of Processing</h3>\n<p>Data is processed exclusively for:</p>\n<ul>\n    <li>User registry management</li>\n    <li>Library loan management</li>\n    <li>Sending loan-related communications (due dates, renewals)</li>\n</ul>\n\n<h3>4. User Rights</h3>\n<p>Users have the right to access their data, request rectification, deletion or restriction of processing, as provided by Regulation EU 2016/679 (GDPR).</p>\n\n<p><strong>IMPORTANT:</strong> This is a sample template. Consult a legal professional to draft a privacy policy compliant with current regulations.</p>\n\n<p><em>Access the <a href=\"/admin/cms/privacy-policy\">admin panel</a> to customize this page.</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `email_templates` VALUES (1,'admin_new_registration','en_US','üë§ New User Registration Request','<h2>New Registration Request</h2>\n<p>A new user has requested access to the library system:</p>\n<p><strong>User Details:</strong></p>\n<ul>\n    <li>Name: {{first_name}} {{last_name}}</li>\n    <li>Email: {{email}}</li>\n    <li>Card Code: {{card_code}}</li>\n    <li>Request Date: {{registration_date}}</li>\n</ul>\n<p><a href=\"{{admin_users_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Manage Users</a></p>','Email sent to admins when a new user registers.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (2,'admin_new_review','en_US','‚≠ê New Review to Approve','<h2>New Review to Approve</h2>\n<p>A new review has been received for the book:</p>\n<p><strong>Book:</strong> {{book_title}}</p>\n<div style=\"background-color: #fff7ed; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #f59e0b;\">\n    <p><strong>Rating:</strong> {{stars}} stars ‚≠ê</p>\n    <p><strong>User:</strong> {{user_name}} ({{user_email}})</p>\n    <p><strong>Review Date:</strong> {{review_date}}</p>\n    <p><strong>Title:</strong> {{review_title}}</p>\n    <p><strong>Description:</strong></p>\n    <p style=\"white-space: pre-line;\">{{review_description}}</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{approval_link}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">Manage Review</a>\n</p>\n<p><em>To approve or reject this review, access the admin panel.</em></p>','Sent to administrators when a new review is received for approval.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (3,'loan_approved','en_US','‚úÖ Your Loan Request Has Been Approved!','<h2>Your Loan Request Has Been Approved!</h2>\n<p>Hello {{user_name}},</p>\n<p>We are pleased to inform you that your loan request has been <strong>approved</strong>!</p>\n<p><strong>Loan Details:</strong></p>\n<ul>\n    <li>Book: {{book_title}}</li>\n    <li>Loan Start Date: {{start_date}}</li>\n    <li>Due Date: {{end_date}}</li>\n    <li>Duration: {{loan_days}} days</li>\n</ul>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Book Pickup</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p><strong>Important:</strong> Remember to return the book by the due date. You will receive a reminder a few days before the deadline.</p>\n<p>Happy reading!</p>','Sent to the user when an administrator approves a loan request.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (4,'loan_rejected','en_US','‚ùå Your Loan Request Was Not Approved','<h2>Your Loan Request Was Not Approved</h2>\n<p>Hello {{user_name}},</p>\n<p>We regret to inform you that your loan request for the book <strong>\"{{book_title}}\"</strong> was not approved.</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Reason:</strong></p>\n    <p>{{rejection_reason}}</p>\n</div>\n<p>If you have any questions or need more information, please don''t hesitate to contact us.</p>\n<p>Best regards,<br>The Library Team</p>','Sent to the user when an administrator rejects a loan request.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (5,'loan_expiring_warning','en_US','‚ö†Ô∏è Your Loan is About to Expire','<h2>Loan Expiration Reminder</h2>\n<p>Hello {{user_name}},</p>\n<p>We remind you that your loan is about to expire:</p>\n<p><strong>Loan details:</strong></p>\n<ul>\n    <li>Book: {{book_title}}</li>\n    <li>Expiration date: {{due_date}}</li>\n    <li>Days remaining: {{days_remaining}}</li>\n</ul>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Action Required</strong></p>\n    <p>To avoid penalties, return the book by the due date or contact the library for a possible renewal.</p>\n</div>\n<p>Thank you for your cooperation!</p>','Email sent to the user 3 days before the loan due date.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (6,'loan_overdue_admin','en_US','Loan #{{loan_id}} Overdue','<h2>Overdue Loan</h2>\n<p>Loan <strong>#{{loan_id}}</strong> has entered status <strong>overdue</strong>.</p>\n<ul>\n  <li><strong>Book:</strong> {{book_title}}</li>\n  <li><strong>User:</strong> {{user_name}} ({{user_email}})</li>\n  <li><strong>Loan Date:</strong> {{loan_date}}</li>\n  <li><strong>Due Date:</strong> {{due_date}}</li>\n</ul>\n<p>Please contact the user to request the book return.</p>','Notifies administrators when a loan becomes overdue.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (7,'loan_overdue_notification','en_US','üö® Overdue Loan - Action Required','<h2>Overdue Loan</h2>\n<p>Hello {{user_name}},</p>\n<p>Your loan is overdue and must be returned immediately:</p>\n<p><strong>Loan details:</strong></p>\n<ul>\n    <li>Book: {{book_title}}</li>\n    <li>Due date: {{due_date}}</li>\n    <li>Days overdue: {{days_overdue}}</li>\n</ul>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>üö® Urgent Action Required</strong></p>\n    <p>The book must be returned immediately. Late returns may result in penalties and suspension of service.</p>\n</div>\n<p>Contact the library immediately to resolve this situation.</p>','Email sent to the user when the loan is overdue.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (8,'loan_request_notification','en_US','üìö New Loan Request','<h2>New Loan Request</h2>\n<p>A new loan request has been received:</p>\n<p><strong>Details:</strong></p>\n<ul>\n    <li>Book: {{book_title}}</li>\n    <li>User: {{user_name}} ({{user_email}})</li>\n    <li>Requested start date: {{start_date}}</li>\n    <li>Requested end date: {{end_date}}</li>\n    <li>Request date: {{request_date}}</li>\n</ul>\n<p><a href=\"{{approve_url}}\" style=\"background-color: #10b981; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Manage Request</a></p>','Email sent to admins when a user requests a loan.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (9,'user_account_approved','en_US','Account Approved - Welcome to the Library!','<h2>Your account has been approved!</h2>\n<p>Hello {{first_name}} {{last_name}},</p>\n<p>We are pleased to inform you that your account has been approved by an administrator.</p>\n<p>You can now access the system and start booking books!</p>\n<p><strong>Your account details:</strong></p>\n<ul>\n    <li>Email: {{email}}</li>\n    <li>Card code: {{card_code}}</li>\n</ul>\n<p><a href=\"{{login_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Login Now</a></p>\n<p>Welcome to our digital library!</p>','Email sent when an admin approves the account directly without requiring email verification.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (10,'user_activation_with_verification','en_US','Activate Your Account - Verify Email','<h2>Your account has been approved!</h2>\n<p>Hello {{first_name}} {{last_name}},</p>\n<p>We are pleased to inform you that your account has been approved by an administrator.</p>\n<p>üìß <strong>Verify your email</strong></p>\n<p>To complete activation, click the button below to verify your email address:</p>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{verification_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">‚úÖ Verify Email</a>\n</p>\n<p><strong>Your account details:</strong></p>\n<ul>\n    <li>Email: {{email}}</li>\n    <li>Card code: {{card_code}}</li>\n</ul>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Important</strong></p>\n    <p>The verification link is valid for 7 days. After verification you will be able to access the system and book books.</p>\n</div>\n<p>If you did not request this registration, you can ignore this email.</p>','Email sent when an admin approves the account requiring email verification.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (11,'user_registration_pending','en_US','Registration Received - Pending Approval','<h2>Welcome {{first_name}} {{last_name}}!</h2>\n<p>Your registration request has been received successfully.</p>\n<p><strong>Account details:</strong></p>\n<ul>\n    <li>Email: {{email}}</li>\n    <li>Card code: {{card_code}}</li>\n    <li>Registration date: {{registration_date}}</li>\n</ul>\n{{verify_section}}\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è≥ Account pending approval</strong></p>\n    <p>Your account is pending approval by an administrator. You will receive a confirmation email once your account has been activated.</p>\n</div>\n<p>Thank you for choosing our library system!</p>','Email sent to the user after registration, when the account is pending approval.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (12,'wishlist_book_available','en_US','üìñ Book from Your Wishlist Now Available!','<h2>Good News!</h2>\n<p>Hello {{user_name}},</p>\n<p>The book you added to your wishlist is now available for loan:</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{book_title}}</h3>\n    <p style=\"margin: 5px 0;\"><strong>Author:</strong> {{book_author}}</p>\n    <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{book_isbn}}</p>\n    <p style=\"margin: 5px 0;\"><strong>Available from:</strong> {{availability_date}}</p>\n</div>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚ú® Book Now!</strong></p>\n    <p>The book is now available for booking. Hurry before someone else books it!</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{book_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üìö Book Now</a>\n    <a href=\"{{wishlist_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">‚ù§Ô∏è Manage Wishlist</a>\n</p>\n<p><em>This book has been automatically removed from your wishlist.</em></p>','Email sent when a wishlist book becomes available.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (13,'reservation_book_available','en_US','üìö Reserved Book Ready for Pickup!','<h2>Your book is ready for pickup!</h2>\n<p>Hello {{user_name}},</p>\n<p>We are pleased to inform you that the book you reserved is now available and ready for pickup:</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{book_title}}</h3>\n    <p style=\"margin: 5px 0;\"><strong>Author:</strong> {{book_author}}</p>\n    <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{book_isbn}}</p>\n    <p style=\"margin: 5px 0;\"><strong>Loan Period:</strong> {{start_date}} - {{end_date}}</p>\n</div>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Next Steps</strong></p>\n    <p>Visit the library to pick up the book. Please bring an ID with you.</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{book_url}}\" style=\"background-color: #3b82f6; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üìñ View Book</a>\n    <a href=\"{{profile_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üë§ My Loans</a>\n</p>\n<p><em>Your reservation has been converted to a loan pending pickup confirmation.</em></p>','Email sent when a reserved book becomes available and is converted to a loan.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (14,'user_password_setup','en_US','üîê Set Up Your Password','<h2>Set Up Your Password</h2>\n<p>Hello {{first_name}} {{last_name}},</p>\n<p>Your account on <strong>{{app_name}}</strong> has been created. To start using the system, you need to set up your password.</p>\n<div style=\"background-color: #f0f9ff; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #3b82f6;\">\n    <p><strong>üîë Configure Your Account</strong></p>\n    <p>Click the button below to set your password:</p>\n</div>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{reset_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">üîê Set Password</a>\n</p>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Important</strong></p>\n    <p>This link is valid for 24 hours. If it expires, contact an administrator to receive a new one.</p>\n</div>\n<p>If you did not request this email, you can ignore it.</p>','Email sent to new users to set up their account password.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (15,'admin_invitation','en_US','üéâ Administrator Invitation','<h2>Welcome to the Team!</h2>\n<p>Hello {{first_name}} {{last_name}},</p>\n<p>You have been invited as an administrator on <strong>{{app_name}}</strong>.</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">Your Access</h3>\n    <p>As an administrator, you will have access to:</p>\n    <ul>\n        <li>Book catalog management</li>\n        <li>User and loan management</li>\n        <li>System settings</li>\n        <li>Reports and statistics</li>\n    </ul>\n</div>\n<p><strong>To get started:</strong></p>\n<ol>\n    <li>Set your password by clicking the button below</li>\n    <li>Access the admin dashboard</li>\n</ol>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{reset_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block; margin: 10px;\">üîê Set Password</a>\n    <a href=\"{{dashboard_url}}\" style=\"background-color: #3b82f6; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block; margin: 10px;\">üìä Admin Dashboard</a>\n</p>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Important</strong></p>\n    <p>The password setup link is valid for 24 hours.</p>\n</div>\n<p>Welcome to the team!</p>','Email sent when a user is invited as an administrator.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (16,'loan_pickup_ready','en_US','üì¶ Book Ready for Pickup!','<h2>Book Ready for Pickup!</h2>\n<p>Hello {{user_name}},</p>\n<p>We are pleased to inform you that your book is ready to be picked up!</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{book_title}}</h3>\n    <p style=\"margin: 5px 0;\"><strong>Loan period:</strong> {{start_date}} - {{end_date}}</p>\n    <p style=\"margin: 5px 0;\"><strong>Pickup deadline:</strong> {{pickup_deadline}}</p>\n</div>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ How to Pick Up</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p>Happy reading!</p>','Sent when a loan is approved and the book is ready for pickup.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (17,'loan_pickup_expired','en_US','‚è∞ Pickup Time Expired','<h2>Pickup Time Expired</h2>\n<p>Hello {{user_name}},</p>\n<p>Unfortunately, you did not pick up the book within the allowed time.</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Book:</strong> {{book_title}}</p>\n    <p><strong>Pickup deadline:</strong> {{pickup_deadline}}</p>\n</div>\n<p>The loan has been automatically cancelled and the book is now available for other users.</p>\n<p>If you still want this book, please submit a new loan request.</p>\n<p>Best regards,<br>The Library Team</p>','Sent when the pickup time expires and the loan is automatically cancelled.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (18,'loan_pickup_cancelled','en_US','‚ùå Pickup Cancelled','<h2>Pickup Cancelled</h2>\n<p>Hello {{user_name}},</p>\n<p>We inform you that the pickup for the following book has been cancelled:</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Book:</strong> {{book_title}}</p>\n    <p><strong>Reason:</strong> {{reason}}</p>\n</div>\n<p>The book is now available for other users. If you still want this book, please submit a new loan request.</p>\n<p>Best regards,<br>The Library Team</p>','Sent when a pickup is cancelled by the administrator.',1,NULL,NULL);
