INSERT INTO `home_content` (section_key, title, subtitle, content, button_text, button_link, is_active, display_order, created_at, updated_at) VALUES
('hero', 'La Tua Biblioteca Digitale', 'Esplora, prenota e gestisci la tua collezione di libri', NULL, 'Esplora il Catalogo', '/catalogo', 1, -2, NOW(), NOW()),
('features_title', 'Perch√© Scegliere Pinakes', 'Un\'esperienza di gestione biblioteca moderna, intuitiva e sempre a portata di mano', NULL, NULL, NULL, 1, 0, NOW(), NOW()),
('feature_1', 'Gestione Semplificata', 'Cataloga i tuoi libri, gestisci prestiti e restituzioni con un\'interfaccia intuitiva e veloce', 'fas fa-tasks', NULL, NULL, 1, 1, NOW(), NOW()),
('feature_2', 'Catalogazione Completa', 'Importa dati da ISBN, organizza per categorie, genera etichette e traccia ogni copia fisica', 'fas fa-book', NULL, NULL, 1, 2, NOW(), NOW()),
('feature_3', 'Sistema di Prestiti', 'Gestisci prestiti, scadenze, rinnovi e notifiche automatiche via email per utenti e staff', 'fas fa-exchange-alt', NULL, NULL, 1, 3, NOW(), NOW()),
('feature_4', 'Open Source & Gratuito', 'Software libero al 100%, senza costi di licenza. Personalizza e contribuisci al progetto Pinakes', 'fas fa-code-branch', NULL, NULL, 1, 4, NOW(), NOW()),
('text_content', 'Œ†ŒØŒΩŒ±Œ∫ŒµœÇ (Pinakes) - "Le Tavole"', NULL, '<p>Il nome <strong>Pinakes</strong> deriva dal greco antico <em>œÄŒØŒΩŒ±Œ∫ŒµœÇ</em> (pinakes), che significa "tavole" o "tavolette". Onora <strong>Callimaco di Cirene</strong> (305-240 a.C.) che intorno al 245 a.C. cre√≤ per la Biblioteca di Alessandria il primo catalogo bibliotecario sistematico della storia: i <em>Œ†ŒØŒΩŒ±Œ∫ŒµœÇ œÑ·ø∂ŒΩ ·ºêŒΩ œÄŒ¨œÉ·øÉ œÄŒ±ŒπŒ¥ŒµŒØ·æ≥ Œ¥ŒπŒ±ŒªŒ±ŒºœàŒ¨ŒΩœÑœâŒΩ Œ∫Œ±·Ω∂ ·ΩßŒΩ œÉœÖŒΩŒ≠Œ≥œÅŒ±œàŒ±ŒΩ</em> ("Tavole di coloro che si sono distinti in ogni campo del sapere e delle loro opere").</p><p>Quest\'opera monumentale, composta da 120 rotoli, organizzava sistematicamente oltre 120.000 rotoli di papiro presenti nella biblioteca. Sebbene i <em>Pinakes</em> originali siano andati perduti, il loro metodo di catalogazione ha influenzato profondamente la biblioteconomia moderna.</p><p>Questo progetto prosegue quella tradizione millenaria di <strong>2.268 anni</strong> portando gli stessi principi di organizzazione e accessibilit√† del sapere nell\'era digitale.</p>', NULL, NULL, 1, 4, NOW(), NOW()),
('latest_books_title', 'Ultimi Arrivi', 'Scopri le ultime novit√† aggiunte al catalogo', NULL, NULL, NULL, 1, 5, NOW(), NOW()),
('genre_carousel', 'Esplora i generi principali', 'Scopri le nostre radici tematiche e lasciati ispirare dai titoli disponibili.', NULL, NULL, NULL, 1, 6, NOW(), NOW()),
('events', 'Eventi in Programma', 'Partecipa ai nostri prossimi incontri', NULL, NULL, NULL, 1, 7, NOW(), NOW()),
('cta', 'Pronto a iniziare?', 'Registrati ora e inizia a esplorare il nostro catalogo', NULL, 'Registrati Ora', '/register', 1, 8, NOW(), NOW());
