INSERT INTO `cms_pages` VALUES (1,'about-us','it_IT','Chi Siamo','<h2>Benvenuto in Pinakes</h2>\n<p>Pinakes √® un sistema di gestione bibliotecaria moderno, intuitivo e completamente gratuito. Costruito per semplificare la catalogazione dei libri, la gestione dei prestiti e l''amministrazione di biblioteche di ogni dimensione.</p>\n\n<h3>üéØ La Nostra Missione</h3>\n<p>Rendere la gestione bibliotecaria accessibile a tutti, senza costi di licenza e senza vincoli. Pinakes √® 100% open source, libero e gratuito per tutti.</p>\n\n<h3>‚ú® Caratteristiche Principali</h3>\n<ul>\n    <li><strong>Catalogazione Intelligente:</strong> Importa automaticamente i dati da ISBN, organizza per categorie, autori e classificazioni personalizzate</li>\n    <li><strong>Gestione Prestiti:</strong> Sistema completo per gestire prestiti, scadenze, rinnovi e restituzioni</li>\n    <li><strong>Notifiche Automatiche:</strong> Promemoria via email per le scadenze dei prestiti e comunicazioni agli utenti</li>\n    <li><strong>Interfaccia Moderna:</strong> Design responsive ottimizzato per desktop, tablet e smartphone</li>\n    <li><strong>100% Open Source:</strong> Codice libero, personalizzabile e senza costi nascosti</li>\n</ul>\n\n<h3>üöÄ Perch√© Scegliere Pinakes?</h3>\n<p>Pinakes nasce dall''esigenza di fornire uno strumento professionale, affidabile e gratuito per la gestione di biblioteche scolastiche, di associazioni e private. Non serve essere esperti informatici: l''interfaccia intuitiva permette a chiunque di iniziare subito.</p>\n\n<h3>üìñ Inizia Oggi</h3>\n<p>Personalizza questa pagina con le informazioni sulla tua biblioteca, il tuo team e i tuoi servizi. Accedi al <a href=\"/admin/cms/about-us\">pannello admin</a> per modificare questo contenuto e renderlo unico.</p>\n\n<p><em>Questo √® un contenuto di esempio. Personalizzalo per raccontare la storia della tua biblioteca!</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `cms_pages` VALUES (2,'privacy-policy','it_IT','Informativa sulla Privacy','<h2>Informativa sulla Privacy</h2>\n<p>Questa √® una pagina segnaposto per l''Informativa sulla Privacy. Personalizza questo contenuto in base alle normative applicabili (GDPR, Codice Privacy) e alle esigenze specifiche della tua biblioteca.</p>\n\n<h3>1. Titolare del Trattamento</h3>\n<p>[Inserisci qui i dati del titolare del trattamento: nome, indirizzo, email, telefono]</p>\n\n<h3>2. Dati Raccolti</h3>\n<p>I dati personali raccolti attraverso questo sistema includono:</p>\n<ul>\n    <li>Dati anagrafici (nome, cognome, data di nascita)</li>\n    <li>Dati di contatto (email, telefono, indirizzo)</li>\n    <li>Dati relativi ai prestiti effettuati</li>\n</ul>\n\n<h3>3. Finalit√† del Trattamento</h3>\n<p>I dati vengono trattati esclusivamente per:</p>\n<ul>\n    <li>Gestione anagrafica degli utenti</li>\n    <li>Gestione dei prestiti bibliotecari</li>\n    <li>Invio di comunicazioni relative ai prestiti (scadenze, rinnovi)</li>\n</ul>\n\n<h3>4. Diritti dell''Interessato</h3>\n<p>Gli utenti hanno diritto di accedere ai propri dati, richiederne la rettifica, la cancellazione o la limitazione del trattamento, come previsto dal Regolamento UE 2016/679 (GDPR).</p>\n\n<p><strong>IMPORTANTE:</strong> Questo √® un modello di esempio. Consulta un professionista legale per redigere un''informativa privacy conforme alle normative vigenti.</p>\n\n<p><em>Accedi al <a href=\"/admin/cms/privacy-policy\">pannello admin</a> per personalizzare questa pagina.</em></p>',NULL,NULL,1,'2025-10-01 10:35:27','2025-10-01 10:35:27');
INSERT INTO `email_templates` VALUES (1,'admin_new_registration','it_IT','üë§ Nuova richiesta di registrazione','\n            <h2>Nuova richiesta di registrazione</h2>\n            <p>Un nuovo utente ha richiesto l\'accesso a Pinakes:</p>\n            <p><strong>Dettagli utente:</strong></p>\n            <ul>\n                <li>Nome: {{nome}} {{cognome}}</li>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n                <li>Data richiesta: {{data_registrazione}}</li>\n            </ul>\n            <p><a href=\"{{admin_users_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Gestisci Utenti</a></p>\n        ','Email inviata agli admin quando un nuovo utente si registra.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (2,'admin_new_review','it_IT','‚≠ê Nuova recensione da approvare','<h2>Nuova recensione da approvare</h2>\n<p>√à stata ricevuta una nuova recensione per il libro:</p>\n<p><strong>Libro:</strong> {{libro_titolo}}</p>\n<div style=\"background-color: #fff7ed; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #f59e0b;\">\n    <p><strong>Valutazione:</strong> {{stelle}} stelle ‚≠ê</p>\n    <p><strong>Utente:</strong> {{utente_nome}} ({{utente_email}})</p>\n    <p><strong>Data recensione:</strong> {{data_recensione}}</p>\n    <p><strong>Titolo:</strong> {{titolo_recensione}}</p>\n    <p><strong>Descrizione:</strong></p>\n    <p style=\"white-space: pre-line;\">{{descrizione_recensione}}</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{link_approvazione}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">Gestisci Recensione</a>\n</p>\n<p><em>Per approvare o rifiutare questa recensione, accedi al pannello admin.</em></p>','Inviata agli amministratori quando viene ricevuta una nuova recensione da approvare.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (3,'loan_approved','it_IT','‚úÖ La tua richiesta di prestito √® stata approvata!','<h2>La tua richiesta di prestito √® stata approvata!</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Siamo lieti di informarti che la tua richiesta di prestito √® stata <strong>approvata</strong>!</p>\n<p><strong>Dettagli del prestito:</strong></p>\n<ul>\n    <li>Libro: {{libro_titolo}}</li>\n    <li>Data inizio prestito: {{data_inizio}}</li>\n    <li>Data scadenza: {{data_fine}}</li>\n    <li>Durata: {{giorni_prestito}} giorni</li>\n</ul>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Ritiro del libro</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p><strong>Importante:</strong> Ricorda di restituire il libro entro la data di scadenza. Riceverai un promemoria alcuni giorni prima della scadenza.</p>\n<p>Buona lettura!</p>','Inviata all\'utente quando un amministratore approva una richiesta di prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (4,'loan_rejected','it_IT','‚ùå La tua richiesta di prestito non √® stata approvata','<h2>La tua richiesta di prestito non √® stata approvata</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Ci dispiace informarti che la tua richiesta di prestito per il libro <strong>\"{{libro_titolo}}\"</strong> non √® stata approvata.</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Motivo:</strong></p>\n    <p>{{motivo_rifiuto}}</p>\n</div>\n<p>Se hai domande o desideri maggiori informazioni, non esitare a contattarci.</p>\n<p>Cordiali saluti,<br>Il team della biblioteca</p>','Inviata all\'utente quando un amministratore rifiuta una richiesta di prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (5,'loan_expiring_warning','it_IT','‚ö†Ô∏è Il tuo prestito sta per scadere','\n            <h2>Promemoria scadenza prestito</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Ti ricordiamo che il tuo prestito sta per scadere:</p>\n            <p><strong>Dettagli prestito:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Data scadenza: {{data_scadenza}}</li>\n                <li>Giorni rimasti: {{giorni_rimasti}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>‚è∞ Azione richiesta</strong></p>\n                <p>Per evitare penali, restituisci il libro entro la data di scadenza o contatta la biblioteca per un eventuale rinnovo.</p>\n            </div>\n            <p>Grazie per la collaborazione!</p>\n        ','Email inviata all\'utente 3 giorni prima della scadenza del prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (6,'loan_overdue_admin','it_IT','Prestito #{prestito_id} in ritardo','<h2>Prestito in ritardo</h2>\n<p>Il prestito <strong>#{{prestito_id}}</strong> √® entrato nello stato <strong>in ritardo</strong>.</p>\n<ul>\n  <li><strong>Libro:</strong> {{libro_titolo}}</li>\n  <li><strong>Utente:</strong> {{utente_nome}} ({{utente_email}})</li>\n  <li><strong>Data prestito:</strong> {{data_prestito}}</li>\n  <li><strong>Data scadenza:</strong> {{data_scadenza}}</li>\n</ul>\n<p>Intervieni per contattare l\'utente e sollecitare la restituzione.</p>','Avvisa gli amministratori quando un prestito entra in ritardo.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (7,'loan_overdue_notification','it_IT','üö® Prestito scaduto - Azione richiesta','\n            <h2>Prestito scaduto</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Il tuo prestito √® scaduto e deve essere restituito immediatamente:</p>\n            <p><strong>Dettagli prestito:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Data scadenza: {{data_scadenza}}</li>\n                <li>Giorni di ritardo: {{giorni_ritardo}}</li>\n            </ul>\n            <div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n                <p><strong>üö® Azione urgente richiesta</strong></p>\n                <p>Il libro deve essere restituito immediatamente. Il ritardo nella restituzione pu√≤ comportare sanzioni e la sospensione del servizio.</p>\n            </div>\n            <p>Contatta immediatamente la biblioteca per risolvere la situazione.</p>\n        ','Email inviata all\'utente quando il prestito √® scaduto.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (8,'loan_request_notification','it_IT','üìö Nuova richiesta di prestito','\n            <h2>Nuova richiesta di prestito</h2>\n            <p>√à stata ricevuta una nuova richiesta di prestito:</p>\n            <p><strong>Dettagli:</strong></p>\n            <ul>\n                <li>Libro: {{libro_titolo}}</li>\n                <li>Utente: {{utente_nome}} ({{utente_email}})</li>\n                <li>Data richiesta inizio: {{data_inizio}}</li>\n                <li>Data richiesta fine: {{data_fine}}</li>\n                <li>Data richiesta: {{data_richiesta}}</li>\n            </ul>\n            <p><a href=\"{{approve_url}}\" style=\"background-color: #10b981; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Gestisci Richiesta</a></p>\n        ','Email inviata agli admin quando un utente richiede un prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (9,'user_account_approved','it_IT','Account approvato - Benvenuto in biblioteca!','\n            <h2>Il tuo account √® stato approvato!</h2>\n            <p>Ciao {{nome}} {{cognome}},</p>\n            <p>Siamo lieti di informarti che il tuo account √® stato approvato da un amministratore.</p>\n            <p>Ora puoi accedere al sistema e iniziare a prenotare libri!</p>\n            <p><strong>Dettagli del tuo account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n            </ul>\n            <p><a href=\"{{login_url}}\" style=\"background-color: #3b82f6; color: white; padding: 10px 20px; text-decoration: none; border-radius: 5px;\">Accedi ora</a></p>\n            <p>Benvenuto nella nostra biblioteca digitale!</p>\n        ','Email inviata quando un admin approva direttamente l\'account senza richiedere verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (10,'user_activation_with_verification','it_IT','Attiva il tuo account - Verifica email','\n            <h2>Il tuo account √® stato approvato!</h2>\n            <p>Ciao {{nome}} {{cognome}},</p>\n            <p>Siamo lieti di informarti che il tuo account √® stato approvato da un amministratore.</p>\n            <p>üìß <strong>Verifica la tua email</strong></p>\n            <p>Per completare l\'attivazione, clicca sul pulsante qui sotto per verificare il tuo indirizzo email:</p>\n            <p style=\"text-align: center; margin: 30px 0;\">\n                <a href=\"{{verification_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">‚úÖ Verifica Email</a>\n            </p>\n            <p><strong>Dettagli del tuo account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n            </ul>\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>‚è∞ Importante</strong></p>\n                <p>Il link di verifica √® valido per 7 giorni. Dopo la verifica potrai accedere al sistema e prenotare libri.</p>\n            </div>\n            <p>Se non hai richiesto questa registrazione, puoi ignorare questa email.</p>\n        ','Email inviata quando un admin approva l\'account richiedendo la verifica email.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (11,'user_registration_pending','it_IT','Registrazione ricevuta - In attesa di approvazione','\n            <h2>Benvenuto {{nome}} {{cognome}}!</h2>\n            <p>La tua richiesta di registrazione √® stata ricevuta con successo.</p>\n            <p><strong>Dettagli account:</strong></p>\n            <ul>\n                <li>Email: {{email}}</li>\n                <li>Codice tessera: {{codice_tessera}}</li>\n                <li>Data registrazione: {{data_registrazione}}</li>\n            </ul>\n            {{sezione_verifica}}\n            <div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>‚è≥ Account in attesa di approvazione</strong></p>\n                <p>Il tuo account √® in attesa di approvazione da parte di un amministratore.\n                Riceverai una email di conferma una volta che l\'account sar√† stato attivato.</p>\n            </div>\n            <p>Grazie per aver scelto Pinakes!</p>\n        ','Email inviata all\'utente dopo la registrazione, quando l\'account √® in attesa di approvazione.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (12,'wishlist_book_available','it_IT','üìñ Libro della tua wishlist ora disponibile!','\n            <h2>Buone notizie!</h2>\n            <p>Ciao {{utente_nome}},</p>\n            <p>Il libro che hai aggiunto alla tua wishlist √® ora disponibile per il prestito:</p>\n            <div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n                <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{libro_titolo}}</h3>\n                <p style=\"margin: 5px 0;\"><strong>Autore:</strong> {{libro_autore}}</p>\n                <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{libro_isbn}}</p>\n                <p style=\"margin: 5px 0;\"><strong>Disponibile da:</strong> {{data_disponibilita}}</p>\n            </div>\n            <div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n                <p><strong>‚ú® Prenota subito!</strong></p>\n                <p>Il libro √® ora disponibile per la prenotazione. Affrettati prima che qualcun altro lo prenoti!</p>\n            </div>\n            <p style=\"text-align: center;\">\n                <a href=\"{{book_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üìö Prenota Ora</a>\n                <a href=\"{{wishlist_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">‚ù§Ô∏è Gestisci Wishlist</a>\n            </p>\n            <p><em>Questo libro √® stato automaticamente rimosso dalla tua wishlist.</em></p>\n        ','Email inviata quando un libro della wishlist diventa disponibile.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (13,'reservation_book_available','it_IT','üìö Libro prenotato pronto per il ritiro!','<h2>Il tuo libro √® pronto per il ritiro!</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Siamo lieti di informarti che il libro che avevi prenotato √® ora disponibile e pronto per il ritiro:</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{libro_titolo}}</h3>\n    <p style=\"margin: 5px 0;\"><strong>Autore:</strong> {{libro_autore}}</p>\n    <p style=\"margin: 5px 0;\"><strong>ISBN:</strong> {{libro_isbn}}</p>\n    <p style=\"margin: 5px 0;\"><strong>Periodo prestito:</strong> {{data_inizio}} - {{data_fine}}</p>\n</div>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Prossimi passi</strong></p>\n    <p>Recati in biblioteca per ritirare il libro. Porta con te un documento di identit√†.</p>\n</div>\n<p style=\"text-align: center;\">\n    <a href=\"{{book_url}}\" style=\"background-color: #3b82f6; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üìñ Vedi Libro</a>\n    <a href=\"{{profile_url}}\" style=\"background-color: #6b7280; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block; margin: 10px;\">üë§ I miei Prestiti</a>\n</p>\n<p><em>La prenotazione √® stata convertita in un prestito in attesa di conferma del ritiro.</em></p>','Email inviata quando un libro prenotato diventa disponibile e viene convertito in prestito.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (14,'user_password_setup','it_IT','üîê Imposta la tua password','<h2>Imposta la tua password</h2>\n<p>Ciao {{nome}} {{cognome}},</p>\n<p>Il tuo account su <strong>{{app_name}}</strong> √® stato creato. Per iniziare ad utilizzare il sistema, devi impostare la tua password.</p>\n<div style=\"background-color: #f0f9ff; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #3b82f6;\">\n    <p><strong>üîë Configura il tuo account</strong></p>\n    <p>Clicca sul pulsante qui sotto per impostare la tua password:</p>\n</div>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{reset_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block;\">üîê Imposta Password</a>\n</p>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Importante</strong></p>\n    <p>Il link √® valido per 24 ore. Se scade, contatta un amministratore per riceverne uno nuovo.</p>\n</div>\n<p>Se non hai richiesto questa email, puoi ignorarla.</p>','Email inviata ai nuovi utenti per impostare la password del loro account.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (15,'admin_invitation','it_IT','üéâ Invito come Amministratore','<h2>Benvenuto nel team!</h2>\n<p>Ciao {{nome}} {{cognome}},</p>\n<p>Sei stato invitato come amministratore su <strong>{{app_name}}</strong>.</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">Le tue credenziali</h3>\n    <p>Come amministratore, avrai accesso a:</p>\n    <ul>\n        <li>Gestione catalogo libri</li>\n        <li>Gestione utenti e prestiti</li>\n        <li>Impostazioni del sistema</li>\n        <li>Report e statistiche</li>\n    </ul>\n</div>\n<p><strong>Per iniziare:</strong></p>\n<ol>\n    <li>Imposta la tua password cliccando il pulsante qui sotto</li>\n    <li>Accedi al pannello di amministrazione</li>\n</ol>\n<p style=\"text-align: center; margin: 30px 0;\">\n    <a href=\"{{reset_url}}\" style=\"background-color: #10b981; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block; margin: 10px;\">üîê Imposta Password</a>\n    <a href=\"{{dashboard_url}}\" style=\"background-color: #3b82f6; color: white; padding: 12px 30px; text-decoration: none; border-radius: 8px; font-size: 16px; display: inline-block; margin: 10px;\">üìä Dashboard Admin</a>\n</p>\n<div style=\"background-color: #fef3c7; padding: 15px; border-radius: 5px; margin: 20px 0;\">\n    <p><strong>‚è∞ Importante</strong></p>\n    <p>Il link per impostare la password √® valido per 24 ore.</p>\n</div>\n<p>Benvenuto nel team!</p>','Email inviata quando un utente viene invitato come amministratore.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (16,'loan_pickup_ready','it_IT','üì¶ Libro pronto per il ritiro!','<h2>Libro pronto per il ritiro!</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Siamo lieti di informarti che il tuo libro √® pronto per essere ritirato!</p>\n<div style=\"background-color: #f0f9ff; padding: 20px; border-radius: 10px; border-left: 4px solid #3b82f6; margin: 20px 0;\">\n    <h3 style=\"color: #1e40af; margin: 0 0 10px 0;\">{{libro_titolo}}</h3>\n    <p style=\"margin: 5px 0;\"><strong>Periodo prestito:</strong> {{data_inizio}} - {{data_fine}}</p>\n    <p style=\"margin: 5px 0;\"><strong>Scadenza ritiro:</strong> {{scadenza_ritiro}}</p>\n</div>\n<div style=\"background-color: #ecfdf5; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #10b981;\">\n    <p><strong>üì¶ Come ritirare</strong></p>\n    <p>{{pickup_instructions}}</p>\n</div>\n<p>Buona lettura!</p>','Inviata quando un prestito √® stato approvato e il libro √® pronto per il ritiro.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (17,'loan_pickup_expired','it_IT','‚è∞ Tempo per il ritiro scaduto','<h2>Tempo per il ritiro scaduto</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Purtroppo non hai ritirato il libro entro il tempo previsto.</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Libro:</strong> {{libro_titolo}}</p>\n    <p><strong>Scadenza ritiro:</strong> {{scadenza_ritiro}}</p>\n</div>\n<p>Il prestito √® stato automaticamente annullato e il libro √® stato reso disponibile per altri utenti.</p>\n<p>Se desideri ancora questo libro, ti invitiamo a effettuare una nuova richiesta di prestito.</p>\n<p>Cordiali saluti,<br>Il team della biblioteca</p>','Inviata quando il tempo per ritirare un libro √® scaduto e il prestito √® stato annullato.',1,NULL,NULL);
INSERT INTO `email_templates` VALUES (18,'loan_pickup_cancelled','it_IT','‚ùå Ritiro annullato','<h2>Ritiro annullato</h2>\n<p>Ciao {{utente_nome}},</p>\n<p>Ti informiamo che il ritiro del seguente libro √® stato annullato:</p>\n<div style=\"background-color: #fef2f2; padding: 15px; border-radius: 5px; margin: 20px 0; border-left: 4px solid #ef4444;\">\n    <p><strong>Libro:</strong> {{libro_titolo}}</p>\n    <p><strong>Motivo:</strong> {{motivo}}</p>\n</div>\n<p>Il libro √® stato reso disponibile per altri utenti. Se desideri ancora questo libro, ti invitiamo a effettuare una nuova richiesta di prestito.</p>\n<p>Cordiali saluti,<br>Il team della biblioteca</p>','Inviata quando un ritiro viene annullato dall''amministratore.',1,NULL,NULL);
